        // Bán kính vòng quay riêng
        dot.x = center.x + dot.orbit * Math.cos(rad);
        dot.y = center.y + dot.orbit * Math.sin(rad);
        // Opacity dao động
        dot.opacity = 0.8 + 0.2 * Math.sin(rad + i);
        // Kích thước dao động
        dot.r = dot.baseR + dot.sizeAmp * Math.abs(Math.sin(now * dot.speed * 0.7 + i));
    });

