<script setup>
import { ref, onMounted, onUnmounted } from 'vue';

const shadowMatrix = ref('1,0,0,1,81.5,100.5');
const STAR_COUNT = 12;
const stars = ref([]);

const ballMatrix1 = ref('1,0,0,1,0,0');
const ballMatrix2 = ref('1,0,0,1,0,0');
const ballMatrix3 = ref('1,0,0,1,0,0');
const ballMatrix4 = ref('1,0,0,1,0,0');

function random(min, max) {
    return Math.random() * (max - min) + min;
}

function resetStar(star) {
    // Lưu vị trí gốc để lắc lư quanh đó
    star.baseX = random(110, 250); // vùng ngang giữa
    star.x = star.baseX;
    star.y = random(100, 180); // bắt đầu phía dưới
    star.radius = random(1, 2.8);
    star.opacity = random(0.9, 1);
    star.scale = random(0.8, 2);
    star.speed = random(0.35, 0.55); // tăng tốc độ bay lên một chút
    star.sway = random(0.1, 0.3); // biên độ lắc lư quanh vị trí gốc
    star.swaySpeed = random(0.8, 1.5); // tốc độ lắc lư
    star.time = random(0, 1000);
}

function animateStars() {
    for (const star of stars.value) {
        star.y -= star.speed;
        star.x += Math.sin(star.time / 24) * star.sway;
        star.opacity -= 0.0025; // mờ dần chậm hơn
        star.time += star.swaySpeed;
        if (star.y < 10 || star.opacity <= 0) {
            resetStar(star);
        }
    }
}

function getGiftShakeDeg() {
    const keyframes = [
        { percent: 0, deg: 0 },
        { percent: 10, deg: -4 },
        { percent: 30, deg: 4 },
        { percent: 50, deg: -4 },
        { percent: 70, deg: 0 },
        { percent: 100, deg: 0 },
    ];
    const period = 1200;
    const now = Date.now();
    const t = ((now % period) / period) * 100;
    let i = 0;
    while (i < keyframes.length - 1 && t > keyframes[i + 1].percent) i++;
    const kf1 = keyframes[i];
    const kf2 = keyframes[i + 1];
    const percentDelta = kf2.percent - kf1.percent;
    const localT = percentDelta === 0 ? 0 : (t - kf1.percent) / percentDelta;
    return kf1.deg + (kf2.deg - kf1.deg) * localT;
}

function animateShadow() {
    // Keyframes của shake-gift:
    // 0%: 0deg, 10%: -4deg, 30%: 4deg, 50%: -4deg, 70%: 0deg, 100%: 0deg
    const keyframes = [
        { percent: 0, deg: 0 },
        { percent: 10, deg: -2 },
        { percent: 30, deg: 2 },
        { percent: 50, deg: -2 },
        { percent: 70, deg: 0 },
        { percent: 100, deg: 0 },
    ];
    const period = 1200; // ms
    const now = Date.now();
    const t = ((now % period) / period) * 100; // phần trăm chu kỳ

    // Tìm 2 keyframe gần nhất
    let i = 0;
    while (i < keyframes.length - 1 && t > keyframes[i + 1].percent) i++;
    const kf1 = keyframes[i];
    const kf2 = keyframes[i + 1];

    // Tính tỷ lệ nội suy
    const percentDelta = kf2.percent - kf1.percent;
    const localT = percentDelta === 0 ? 0 : (t - kf1.percent) / percentDelta;
    // Góc lắc bóng nhỏ hơn hộp quà (ví dụ 60%)
    const deg = kf1.deg + (kf2.deg - kf1.deg) * localT;
    const shadowDeg = deg * 0.6;
    const angle = (shadowDeg * Math.PI) / 180;
    const cos = Math.cos(angle);
    const sin = Math.sin(angle);
    shadowMatrix.value = `${cos},${sin},${-sin},${cos},81.5,100.5`;
}

function animateBalls() {
    const giftDeg = getGiftShakeDeg();
    // Mỗi ball lệch biên độ một chút cho tự nhiên
    const ballDegs = [
        giftDeg + giftDeg * 0.1,
        giftDeg + giftDeg * -0.1,
        giftDeg + giftDeg * 0.2,
        giftDeg + giftDeg * -0.2,
    ];
    [ballMatrix1, ballMatrix2, ballMatrix3, ballMatrix4].forEach((matrix, idx) => {
        // Chỉ lắc thêm phần chênh lệch
        const deltaDeg = ballDegs[idx] - giftDeg;
        const angle = (deltaDeg * Math.PI) / 180;
        const cos = Math.cos(angle);
        const sin = Math.sin(angle);
        matrix.value = `${cos},${sin},${-sin},${cos},0,0`;
    });
}

// Sử dụng requestAnimationFrame để đồng bộ tối đa, không bị delay
let animationFrameId = null;
function loop() {
    animateStars();
    animateShadow();
    animateBalls();
    animationFrameId = requestAnimationFrame(loop);
}

onMounted(() => {
    for (let i = 0; i < STAR_COUNT; i++) {
        const star = {};
        resetStar(star);
        star.id = i;
        stars.value.push(star);
    }
    loop();
});

onUnmounted(() => {
    if (animationFrameId) cancelAnimationFrame(animationFrameId);
});
</script>

